		// TODO Auto-generated method stub
		
		// String is just 1 example of non primitive data type
		
		String name="Mollik"; // anything you put inside a "" -> became a string
		String address="123 address street";
		String phone="123 567 7890";
		int age=17;
		System.out.println(); // ctrl+space auto complete the statement.
		
		System.out.println("My name is"+name);		
		
		System.out.println(name+ " is "+ age);
		
		String fruit = "apple";
		double price=1.99;
		System.out.println("The price of the"+fruit+"is" +price);
		
		fruit="mango";
		price=5.99;
		
		System.out.println("The price of the"+fruit+"is"+price);
		
		
		
		// Rules for identifiers=names (variable, methoid)
