package com.sytax.class01;

public class RemitiveDataTypes {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//storing and representing whole number values;
		
		byte box1= 127;	
	   short box2= 32;
	   int box3=100000; //popular
	   long box4=10000000L;
	   
	   // storing and representing decimal values;

	   float variable1=99F;
	   double variable2=99.99; //popular
	   
	   // storing single character
	   
	    char container='a';
	    char dollar='$';
	    
	    // storing yes or no
	    boolean hot=true;	
	    boolean tired=false;
	    
	    // we cannot have duplicate variable inside the same method.
			
	    System.out.println(container);
	    System.out.println(dollar);
	    System.out.println(hot);
	    System.out.println(tired);
	    
	   
	}

}
